import React from 'react';
import './App.css';

// Import from MUI
import Button from '@mui/material/Button';
import Input from '@mui/joy/Input';
import Grid from '@mui/system/Unstable_Grid';
import Box from '@mui/material/Box';
import LoadingButton from '@mui/lab/LoadingButton';


function App() {

  // define a state to hold the response from the API
  const [response, setResponse] = React.useState('')
  const [query, setQuery] = React.useState('Nothing Yet')
  const [loading, setLoading] = React.useState(false)

  // define a function to get the text from the query and submit it to the APi
  function getTextFromApi() {
    // set the loading state to true
    setLoading(true)

    fetch(`http://127.0.0.1:8000/run?query=${query}`)
      .then(response => response.json())
      .then(data => {
        // log the information to the console
        console.log(data)
        // get out the `response` field from the dict and set it to the state
        setResponse(data.response)

      })
  }

  return (
    <div className="App">
      <Grid container spacing={2}>
        <Grid xs={12} sx={{ m: 4 }}>
          <Box>
            <h1>Chain Texts</h1>
            <p>Enter a prompt and generate a response</p>
            <p>Example: "Generate a raunchy chain message about St Patricks Day"</p>
          </Box>
        </Grid>

        <Grid xs={12} sx={{ m: 5 }}>
          <Grid xs={8}>
            <Input type="text" onChange={(e) => setQuery(e.target.value)} />
          </Grid>
          <Grid xs={4}>
            <LoadingButton 
              onClick={() => getTextFromApi()}
              variant="contained"
              color="primary"
              sx={{ m: 4 }}
            >
                Generate Response
            </LoadingButton>
          </Grid>
        </Grid>

        <Grid xs={12}>
          <Box sx={{ m: 4 }}>
            {response}
          </Box>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
