import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  const [text, setText] = React.useState('')
  const [query, setQuery] = React.useState('')

  // define a function to get the text from the query and submit it to the APi
  function getTextFromApi() {
    // get query text 
    fetch(`http://127.0.0.1:8000/run?query=${query}`)
      .then(response => response.json())
      .then(data => setText(data.text))
  }

  function handleClick() {
    setText('Hello World')
  }

  return (
    <div className="App">
      <input type="text" value={text} onChange={(e) => setQuery(e.target.value)} />
      <button onClick={() => getTextFromApi()}>Click me</button>
      <div>{text}</div>
    </div>
  );
}

export default App;
